import React, { useEffect, useState } from 'react';
import { Row, Col, Avatar, Card, Typography, Divider, Image, Space, Button, Modal, notification } from 'antd';
import { StarFilled, UserOutlined, DeleteOutlined, ExclamationCircleOutlined, EditOutlined } from '@ant-design/icons';
import { withRouter } from 'next/router';
import { connect } from 'react-redux';
import axios from 'axios';
import moment from 'moment';
const { Title, Text } = Typography;
const { confirm } = Modal;

function mapStateToProps(state) {
  return {
    user: state.user,
  }
}

const DriverDetailProps = (props) => {
  const {isDeletable, editDrawer} = props;
  const [loading, setLoadin] = useState(true);
  const [detail, setDetail] = useState({
    email: '',
    lastname: '',
    name: '',
    photo: '',
    driver: {
      address: '',
      birthDate: '',
      description: '',
      dln: '',
      expDateDln: '',
      experience: null,
      phoneNumber: null,
      sex: 0,
      zipCode: ''
    },
    jobsComments: [{
      comment: '',
      ranking: 0,
      company: {
        tradename: ''
      }
    }]
  });

  useEffect(() => {
    setDetail(props.driverDetail)
  }, [props.driverDetail]);

  const DescriptionItem = ({ title, content }) => (
    <div className="site-description-item-profile-wrapper">
      <p className="site-description-item-profile-p-label">{title}:</p>
      {content}
    </div>
  );

  function showConfirm(id) {
    confirm({
      title: 'Do you Want to unlink these driver?',
      icon: <ExclamationCircleOutlined />,
      content: 'This action can not be undone',
      onOk() {
        unlinkDriver(id);
      },
      onCancel() {
      },
    });
  }

  const unlinkDriver = async (id) => {
    console.log('HEADER', props.header);
    await axios.patch('/api/company/jobs/unlink/' + id, {}, props.header)
      .then((response) => {
        notification['success']({
          message: 'Success',
          description:
            "Congratulation! Driver successfully disengaged"
        });
        props.fetchStaffList();
      })
      .catch((err) => {
        console.log(err);
        notification['error']({
          message: 'Error',
          description:
            "An error occurred unlinking the driver, please try again"
        });
      })
  }

  const resoltSexType = (props) => {
    let sexType
    if (props == 0) {
      sexType = 'Man'
    } else if (props == 1) {
      sexType = 'Woman'
    } else {
      sexType = 'Other'
    }
    return sexType;
  }

  return (
    <>
      <Row>
        <Col xs={24} xl={4}>
          <Avatar shape="square" size={120} src={detail.photo} icon={<UserOutlined />} />
        </Col>
        <Col xs={24} xl={10}>
          <DescriptionItem title="Full Name" content={`${detail.name} ${detail.lastname}`} />
          <DescriptionItem title="Birthday" content={`${moment(detail.driver.birthDate).format('YYYY-MM-DD')}`} />
          <DescriptionItem title="Phone Number" content={`${detail.driver.phoneNumber}`} />
          <DescriptionItem title="Email" content={`${detail.email}`} />
        </Col>
        <Col xs={24} xl={10}>
          <DescriptionItem title="Sex" content={resoltSexType(detail.driver.sex)} />
          <DescriptionItem title="Address" content={detail.driver.address} />
          <DescriptionItem title="DLN numbers" content={detail.driver.dln} />
          <DescriptionItem title="Exp Dln" content={moment(detail.driver.expDateDln).format('YYYY-MM-DD')} />
        </Col>
      </Row>
      <Divider />
      <Row>
        <Col xs={24} xl={12}>
          {Array.isArray(detail.driver.experience) &&
            <span>
              <p className="site-description-item-profile-p">Experience</p>
              {detail.driver.experience.map((i) => {
                return <Col span={12}>
                  {i.have ? <DescriptionItem title={i.name} content={`${i.years} years`} /> : <DescriptionItem title={i.name} content='Does not have' />}
                </Col>
              })}
            </span>
          }
        </Col>
        <Col xs={24} xl={12}>
          <Row>
            <Col span={24}>
              <Title level={5}>DLN</Title>
              <Image
                width={250}
                src={detail.driver.imageDln}
                fallback="data:image/png;base64,"
              />
            </Col>
            <Col span={24}>
              <Title level={5}>Medic Card</Title>
              <Image
                width={250}
                src={detail.driver.medicCardImage}
                fallback="data:image/png;base64,"
              />
            </Col>
          </Row>
        </Col>
      </Row>
      <Divider />
      {detail.jobsComments && detail.jobsComments.length > 0 && <>
        <Divider />
        <p className="site-description-item-profile-p">Comments and rating</p>
        {detail.jobsComments.map((i) => {
          return <Row>
            <Col span={24}>
              <Card title={i.company.tradename} extra={<Space>
                <StarFilled style={{ fontSize: '24px', color: '#ffce00' }} />
                <span> {i.ranking} </span>
              </Space>}>
                <p>{i.comment}</p>
              </Card>
            </Col>
          </Row>
        })}
      </>
      }
      {isDeletable &&
      <Row>
        <Col span={12} style={{textAlign:"center"}}>
          <Button
            icon={<EditOutlined />}
            shape="round"
            size="large"
            onClick={() => editDrawer(detail)}>
            Edit Driver
          </Button>
        </Col>
        <Col span={12} style={{textAlign:"center"}}>
          <Button
            icon={<DeleteOutlined />}
            shape="round"
            size="large"
            type='primary'
            onClick={() => showConfirm(detail.id)}>
            Unlink Driver
          </Button>
        </Col>
      </Row>
      }
    </>
  )
};


export default withRouter(connect(mapStateToProps)(DriverDetailProps));